// import React from "react";
import {useState, useEffect} from 'react'
import NewListing from './NewListing'
import list from '../disney.jpg'
import EditListing from './EditListing'
import axios from 'axios'

const Home = () => {

  const [listings, setListings]=useState([])
  const handleDelete = (listingData) => {
    axios
      .delete(`https://re-play-back.herokuapp.com/api/listings/${listingData._id}`)
      .then(()=> {
        axios
          .get('https://re-play-back.herokuapp.com/api/listings').then((response)=> {
            setListings(response.data)
          })
      })
  }
  useEffect(()=>{
    axios.get('http://re-play-back.herokuapp.com/api/listings').then((response)=>{
      setListings(response.data)
    })
  })
    return (
      <div className='container'>
  //       <div className="card" >
  // <img className="card-img-top" src="..." alt="Card image cap" />
  // <img className="card-img-top" src={list} alt="Card image cap" />
  // <div className="card-body">
  //   <h3 className="card-title text-warning">Disney Sports Basketball</h3>
  //   <p className="card-text text-info">Rare Score(1-10): 11</p>
  //   <p className="card-text text-info">Asking Price: $550.00</p>
  //   <p className="card-text text-info">Condition: Great</p>
  //   <a href="#" className="btn btn-success d-flex justify-content-between">Buy it Now</a>
  //   <a href="#" className="btn btn-danger d-flex justify-content-between">Delete</a>
  // </div>
  // </div>
    {listings.map((listing)=>{
      return <div className='card' key={listing.id}>
      <img className="card-img-top" src={listing.image} alt="Card image cap" />
      <div className="card-body">
        <h3 className="card-title text-warning">{listing.name}</h3>
        <p className="card-text text-info">{listing.description}</p>
        <p className="card-text text-info">Asking Price: {listing.price}</p>
        <p className="card-text text-info">Condition: {listing.condition}</p>
        <a href="#" className="btn btn-success d-flex justify-content-between">Buy it Now</a>
        <button className="btn btn-danger d-flex justify-content-between" onClick={handleDelete}>Delete</button>
      </div>
      </div>
    })}
    </div>
  )

}

export default Home
